document.addEventListener('DOMContentLoaded', () => { const overlayText = document.getElementById('overlay-text'); const overlay = document.getElementById('overlay'); const centerBox = document.getElementById('center-box'); setTimeout(() => overlayText.classList.add('visible'), 250); const gradient = new Gradient(); gradient.initGradient('#gradient-canvas'); playRandomMP3(); }); document.getElementById('overlay-text').addEventListener('click', function() { const overlay = document.getElementById('overlay'); overlay.classList.add('hidden'); setTimeout(() => { overlay.style.display = 'none'; const centerBox = document.getElementById('center-box'); centerBox.style.display = 'block'; setTimeout(() => { centerBox.style.opacity = 1; centerBox.style.transform = 'translate(-50%, -50%) scale(1)'; setTimeout(() => { centerBox.style.animation = 'pulse 2s infinite ease-in-out'; }, 2000); }, 500); playRandomMP3(); }, 1000); }); function playRandomMP3() { const audioFiles = ['sounds/sound0.mp3', 'sounds/sound1.mp3', 'sounds/sound2.mp3', 'sounds/sound3.mp3', 'sounds/sound4.mp3', 'sounds/sound5.mp3']; let lastPlayed = []; function getRandomFile() { let availableFiles = audioFiles.filter(file => !lastPlayed.includes(file)); if (availableFiles.length === 0) { lastPlayed = lastPlayed.slice(1); availableFiles = audioFiles.filter(file => !lastPlayed.includes(file)); } const randomIndex = Math.floor(Math.random() * availableFiles.length); const randomFile = availableFiles[randomIndex]; lastPlayed.push(randomFile); if (lastPlayed.length > 3) lastPlayed.shift(); return randomFile; } function playNext() { const audio = new Audio(getRandomFile()); audio.play(); audio.onended = playNext; } playNext(); } let firstMove = true; let particleInterval; let mouseX, mouseY; document.addEventListener("DOMContentLoaded", () => { document.addEventListener("mousemove", function(e) { mouseX = e.clientX; mouseY = e.clientY; if (firstMove) { firstMove = false; createParticles(mouseX, mouseY, true, "particle", 15, 15); particleInterval = setInterval(() => { createParticles(mouseX, mouseY, false, "particle", 15, 15); }, 100); } }); function createParticles(x, y, initial, className, spreadX, spreadY) { for (let i = 0; i < 1; i++) { createParticle(x, y, initial, className, spreadX, spreadY); } } function createParticle(x, y, initial, className, spreadX, spreadY) { const particle = document.createElement("div"); particle.classList.add(className); if (initial) particle.classList.add("first-move"); document.body.appendChild(particle); particle.style.backgroundColor = ["#656a95", "#0c0d0d"][Math.floor(Math.random() * 2)]; particle.style.left = `${x + (Math.random() - 0.5) * spreadX}px`; particle.style.top = `${y + (Math.random() - 0.5) * spreadY}px`; particle.style.animationDuration = `${0.5 * Math.random() + 0.5}s`; particle.style.transform = `translateX(${(Math.random() - 0.5) * spreadX}px) translateY(${(Math.random() - 0.5) * spreadY}px)`; setTimeout(() => { particle.remove(); }, 500); } });
