document.addEventListener('DOMContentLoaded',()=>{setTimeout(()=>{document.getElementById('start').style.opacity='1'},1000)});document.addEventListener('contextmenu',event=>event.preventDefault());document.getElementById('start').addEventListener('click',()=>{const start=document.getElementById('start');const background=document.getElementById('background');const square=document.getElementById('square');background.src=`photos/gif${Math.floor(Math.random()*12)+1}.gif`;background.style.display='block';background.style.opacity='1';background.style.filter='blur(0px)';square.style.display='block';setTimeout(()=>{square.style.opacity='1';square.style.transform='scale(1)'},500);setTimeout(()=>{square.style.opacity='1';square.style.animation='pulse 2s infinite ease-in-out'},4000);start.style.transition='opacity 0.5s ease-in-out';start.style.opacity='0';setTimeout(()=>{start.style.display='none'},500);start.classList.add('clicked');playRandomSound();setTimeout(()=>{createParticlesAroundElement(document.getElementById('username'),80,30)},3000)});document.getElementById('start').addEventListener('mouseover',function(){if(!this.classList.contains('clicked')){this.style.color='grey'}});document.getElementById('start').addEventListener('mouseout',function(){if(!this.classList.contains('clicked')){this.style.color='white'}});const sounds=['sounds/sound1.mp3','sounds/sound2.mp3','sounds/sound3.mp3','sounds/sound4.mp3','sounds/sound5.mp3','sounds/sound6.mp3','sounds/sound7.mp3','sounds/sound8.mp3'];let lastPlayed=[];function playRandomSound(){let availableSounds=sounds.filter(sound=>!lastPlayed.includes(sound));if(availableSounds.length===0){lastPlayed=lastPlayed.slice(1);availableSounds=sounds.filter(sound=>!lastPlayed.includes(sound))}const randomSound=availableSounds[Math.floor(Math.random()*availableSounds.length)];const audio=new Audio(randomSound);audio.play();lastPlayed.push(randomSound);if(lastPlayed.length>2){lastPlayed.shift()}audio.onended=playRandomSound}let intervalId;let firstMouseMove=true;let mouseX=0;let mouseY=0;document.addEventListener('mousemove',function(e){mouseX=e.clientX;mouseY=e.clientY;if(firstMouseMove){firstMouseMove=false;intervalId=setInterval(()=>{createParticles(mouseX,mouseY,false,'particle',15,15)},100)}});function createParticles(x,y,isFirstMove,particleClass='particle',spreadWidth=10,spreadHeight=10){for(let i=0;i<1;i++){createParticle(x,y,isFirstMove,particleClass,spreadWidth,spreadHeight)}}function createParticle(x,y,isFirstMove,particleClass='particle',spreadWidth=10,spreadHeight=10){const particle=document.createElement('div');particle.classList.add(particleClass);if(isFirstMove){particle.classList.add('first-move')}document.body.appendChild(particle);particle.style.left=`${x+(Math.random()-0.5)*spreadWidth}px`;particle.style.top=`${y+(Math.random()-0.5)*spreadHeight}px`;particle.style.animationDuration=`${Math.random()*0.5+0.5}s`;particle.style.transform=`translateX(${(Math.random()-0.5)*spreadWidth}px) translateY(${(Math.random()-0.5)*spreadHeight}px)`;setTimeout(()=>{particle.remove()},500)}function createParticlesAroundElement(element,spreadWidth=10,spreadHeight=10){function animate(){const rect=element.getBoundingClientRect();const x=rect.left+rect.width/2;const y=rect.top+rect.height/2;createParticles(x,y,false,'text-particle',spreadWidth,spreadHeight);requestAnimationFrame(animate)}requestAnimationFrame(animate)}